-- Name: is_coupon_valid(character varying, uuid); Type: FUNCTION; Schema: public; Owner: postgres
--

CREATE FUNCTION public.is_coupon_valid(p_code character varying, p_user_id uuid) RETURNS TABLE(valid boolean, message text, coupon_data jsonb)
    LANGUAGE plpgsql
    AS $$
DECLARE
    v_coupon RECORD;
    v_usage_count INTEGER;
BEGIN
    -- Get coupon details
    SELECT * INTO v_coupon
    FROM coupons
    WHERE UPPER(code) = UPPER(p_code)
      AND is_active = true;
    
    -- Check if coupon exists
    IF v_coupon IS NULL THEN
        RETURN QUERY SELECT 
            false, 
            'Kupon kodu bulunamadı'::TEXT,
            NULL::JSONB;
        RETURN;
    END IF;
    
    -- Check validity dates
    IF v_coupon.valid_from > NOW() THEN
        RETURN QUERY SELECT 
            false, 
            'Kupon henüz aktif değil'::TEXT,
            NULL::JSONB;
        RETURN;
    END IF;
    
    IF v_coupon.valid_until IS NOT NULL AND v_coupon.valid_until < NOW() THEN
        RETURN QUERY SELECT 
            false, 
            'Kupon süresi dolmuş'::TEXT,
            NULL::JSONB;
        RETURN;
    END IF;
    
    -- Check max uses
    IF v_coupon.max_uses IS NOT NULL AND v_coupon.uses_count >= v_coupon.max_uses THEN
        RETURN QUERY SELECT 
            false, 
            'Kupon kullanım limiti dolmuş'::TEXT,
            NULL::JSONB;
        RETURN;
    END IF;
    
    -- Check if user has already used this coupon (for one-time use coupons)
    IF v_coupon.one_time_per_user THEN
        SELECT COUNT(*) INTO v_usage_count
        FROM coupon_usage
        WHERE coupon_id = v_coupon.id AND user_id = p_user_id;
        
        IF v_usage_count > 0 THEN
            RETURN QUERY SELECT 
                false, 
                'Bu kuponu daha önce kullandınız'::TEXT,
                NULL::JSONB;
            RETURN;
        END IF;
    END IF;
    
    -- Coupon is valid
    RETURN QUERY SELECT 
        true,
        'Kupon geçerli'::TEXT,
        to_jsonb(v_coupon);
END;
$$;


